var api_incidentevidence=window.location.origin+"/api/incidentevidence/";var form=null;$(document).ready(function(){form=new Formulario()});function Formulario(){this.$btn_upload=$("#btn-upload");this.$fileupload=$("#fileupload");this.$empty_section=$("#empty-section-message");this.$gallery=$("#gallery");this.init_Components();this.set_Events()}Formulario.prototype.init_Components=function(){this.$fileupload.fileupload({dataType:"json",url:api_incidentevidence,sequentialUploads:true,replaceFileInput:false,start:function(a){$("#modal-progress").modal("show")},stop:function(a){$("#modal-progress").modal("hide")},progressall:function(d,c){var a=parseInt(c.loaded/c.total*100,10);var b=a+"%";$(".progress-bar").css({width:b});$(".progress-bar").text(b)},done:this.show_FileUploaded,error:function(a){mensaje="";$.each(a.responseJSON,function(c,b){mensaje=mensaje+c+" :"+b[0]+"\n"});alertify.error(mensaje)}})};Formulario.prototype.set_Events=function(){this.$btn_upload.on("click",this,this.click_btn_upload)};Formulario.prototype.show_FileUploaded=function(b,a){form.$empty_section.hide();form.$gallery.find("tbody").prepend("<tr><td><a href='"+a.result.url+"'>"+a.result.name+"</a></td></tr>")};Formulario.prototype.click_btn_upload=function(a){a.data.$fileupload.click()};